import React, { useState } from 'react'
import Inpform from './Inpform';
import Todolist from './Todolist';

const Todoapp = () => {

  const dummy_todos = [
    { id: 1, text: 'Swimming' },
    { id: 2, text: 'Sleeping' },
    { id: 3, text: 'Crying' },
    { id: 4, text: 'Living' },
  ]

  const [todos,setTodos] = useState(dummy_todos);

  function addtodo(todo){
    const arr = [...todos,todo]         //spread operator
    setTodos(arr)
  }

  return (
    <div>
      <Inpform addtodo={addtodo} dummy={todos}/>
      <Todolist todos={todos}/>
    </div>
  )
}

export default Todoapp
